"use client"

import { RefObject, useEffect, useId, useState } from "react"
import { motion } from "motion/react"

import { cn } from "@/lib/utils"

export interface AnimatedBeamProps {
  className?: string
  containerRef: RefObject<HTMLElement | null> // Container ref
  fromRef: RefObject<HTMLElement | null>
  toRef: RefObject<HTMLElement | null>
  curvature?: number
  reverse?: boolean
  pathColor?: string
  pathWidth?: number
  pathOpacity?: number
  gradientStartColor?: string
  gradientStopColor?: string
  delay?: number
  duration?: number
  startXOffset?: number
  startYOffset?: number
  endXOffset?: number
  endYOffset?: number
}

export const AnimatedPath: React.FC<AnimatedBeamProps> = ({
  className,
  containerRef,
  fromRef,
  toRef,
  reverse = false, // Include the reverse prop
  curvature = 0,
  duration = Math.random() * 3 + 4,
  delay = 0,
  pathColor = "gray",
  pathWidth = 2,
  pathOpacity = 0.1,
  gradientStartColor = "#ffaa40",
  gradientStopColor = "#9c40ff",
  startXOffset = 0,
  startYOffset = 0,
  endXOffset = 0,
  endYOffset = 0,
}) => {
  const id = useId()
  const [pathD, setPathD] = useState("")
  const [svgDimensions, setSvgDimensions] = useState({ width: 0, height: 0 })

  const gradientCoordinates = reverse
    ? {
        x1: ["90%", "-10%"],
        x2: ["100%", "0%"],
        y1: ["0%", "0%"],
        y2: ["0%", "0%"],
      }
    : {
        x1: ["10%", "110%"],
        x2: ["0%", "100%"],
        y1: ["0%", "0%"],
        y2: ["0%", "0%"],
      }

  useEffect(() => {
    const updatePath = () => {
      if (containerRef.current && fromRef.current && toRef.current) {
        const containerRect = containerRef.current.getBoundingClientRect()
        const rectA = fromRef.current.getBoundingClientRect()
        const rectB = toRef.current.getBoundingClientRect()

        const svgWidth = containerRect.width
        const svgHeight = containerRect.height
        setSvgDimensions({ width: svgWidth, height: svgHeight })

        const startX =
          rectA.left - containerRect.left + rectA.width / 2 + startXOffset
        const startY =
          rectA.top - containerRect.top + rectA.height / 2 + startYOffset
        const endX =
          rectB.left - containerRect.left + rectB.width / 2 + endXOffset
        const endY =
          rectB.top - containerRect.top + rectB.height / 2 + endYOffset

        const controlY = startY - curvature
        const d = `M ${startX},${startY} Q ${
          (startX + endX) / 2
        },${controlY} ${endX},${endY}`
        setPathD(d)
      }
    }

    // Initialize ResizeObserver
    const resizeObserver = new ResizeObserver(() => {
      updatePath()
    })

    // Observe the container element
    if (containerRef.current) {
      resizeObserver.observe(containerRef.current)
    }

    // Call the updatePath initially to set the initial path
    updatePath()

    // Clean up the observer on component unmount
    return () => {
      resizeObserver.disconnect()
    }
  }, [
    containerRef,
    fromRef,
    toRef,
    curvature,
    startXOffset,
    startYOffset,
    endXOffset,
    endYOffset,
  ])

  return (
    <svg
      fill="none"
      width={svgDimensions.width}
      height={svgDimensions.height}
      xmlns="http://www.w3.org/2000/svg"
      className={cn(
        "pointer-events-none absolute top-0 left-0 transform-gpu stroke-2",
        className
      )}
      viewBox={`0 0 ${svgDimensions.width} ${svgDimensions.height}`}
    >
      <path
        d={pathD}
        stroke={pathColor}
        strokeWidth={pathWidth}
        strokeOpacity={pathOpacity}
        strokeLinecap="round"
      />
      <path
        d={pathD}
        strokeWidth={pathWidth}
        stroke={`url(#${id})`}
        strokeOpacity="1"
        strokeLinecap="round"
      />
      <defs>
        <motion.linearGradient
          className="transform-gpu"
          id={id}
          gradientUnits={"userSpaceOnUse"}
          initial={{
            x1: "0%",
            x2: "0%",
            y1: "0%",
            y2: "0%",
          }}
          animate={{
            x1: gradientCoordinates.x1,
            x2: gradientCoordinates.x2,
            y1: gradientCoordinates.y1,
            y2: gradientCoordinates.y2,
          }}
          transition={{
            delay,
            duration,
            ease: [0.16, 1, 0.3, 1], // https://easings.net/#easeOutExpo
            repeat: Infinity,
            repeatDelay: 0,
          }}
        >
          <stop stopColor={gradientStartColor} stopOpacity="0"></stop>
          <stop stopColor={gradientStartColor}></stop>
          <stop offset="32.5%" stopColor={gradientStopColor}></stop>
          <stop
            offset="100%"
            stopColor={gradientStopColor}
            stopOpacity="0"
          ></stop>
        </motion.linearGradient>
      </defs>
    </svg>
  )
}

"use client"

import React, { ComponentProps, useRef } from "react"
import {
  Chrome,
  Facebook,
  Framer,
  Github,
  Instagram,
  Linkedin,
  Slack,
  Twitter,
  Youtube,
} from "lucide-react"

import { cn } from "@/lib/utils"
import { AnimatedPath } from "@/registry/components/animated-path"
import { Card, CardContent } from "@/components/ui/card"

export default function AnimatedConnectLine() {
  const containerRef = useRef<HTMLDivElement>(null)
  const div1Ref = useRef<HTMLDivElement>(null)
  const div2Ref = useRef<HTMLDivElement>(null)
  const div3Ref = useRef<HTMLDivElement>(null)
  const div4Ref = useRef<HTMLDivElement>(null)
  const div5Ref = useRef<HTMLDivElement>(null)
  const div6Ref = useRef<HTMLDivElement>(null)
  const div7Ref = useRef<HTMLDivElement>(null)
  const div8Ref = useRef<HTMLDivElement>(null)
  const div9Ref = useRef<HTMLDivElement>(null)
  return (
    <Card className="relative w-full max-w-sm bg-surface" ref={containerRef}>
      <CardContent className="grid grid-cols-3 place-items-center justify-items-center gap-x-8 gap-y-12 py-8">
        <Box ref={div1Ref} icon={Framer} label="Framer" />
        <Box ref={div2Ref} icon={Github} label="Github" />
        <Box ref={div3Ref} icon={Twitter} label="Twitter" />
        <Box ref={div4Ref} icon={Instagram} label="Instagram" />
        <Box ref={div5Ref} icon={Linkedin} label="Linkedin" />
        <Box ref={div6Ref} icon={Facebook} label="Facebook" />
        <Box ref={div7Ref} icon={Youtube} label="Youtube" />
        <Box ref={div8Ref} icon={Chrome} label="Chrome" />
        <Box ref={div9Ref} icon={Slack} label="Slack" />
      </CardContent>
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div1Ref}
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div2Ref}
        reverse
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div3Ref}
        reverse
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div4Ref}
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div6Ref}
        reverse
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div7Ref}
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div8Ref}
      />
      <AnimatedPath
        containerRef={containerRef}
        fromRef={div5Ref}
        reverse
        gradientStartColor="#f5ae51"
        gradientStopColor="#d18015"
        toRef={div9Ref}
      />
    </Card>
  )
}

function Box({
  icon: Icon,
  className,
  label,
  ref,
}: ComponentProps<"div"> & { icon: React.ElementType; label: string }) {
  return (
    <div
      className={cn(
        "font-inter font-normal tracking-wide w-fit grid justify-items-center gap-y-1 z-10",
        className
      )}
      ref={ref}
    >
      <div className="border bg-accent rounded-2xl p-4 flex items-center justify-center w-fit h-fit">
        <Icon className="size-6" />
      </div>
      <h4 className="text-xs text-muted-foreground">{label}</h4>
    </div>
  )
}
